import randomdef jogar():    print("*********************************")    print("Bem-vindo ao jogo de adivinhação!")    print("*********************************")    print("Selecione o nível de dificuldade: ")    print("1 - Fácil")    print("2 - Médio")    print("3 - Difícil")    nivel_str = input(":")    nivel = int(nivel_str)    if (nivel == 1):        total_tentativas = 20    elif (nivel == 2):        total_tentativas = 10    else:        total_tentativas = 5    pontuacao = 1000    numero_secreto = random.randrange(1, 101)    for rodada in range(1, total_tentativas + 1):        mensagem_tentativa = "Tentativa {} de {}".format(rodada, total_tentativas)        print(mensagem_tentativa)        chute_str = input("Digite o seu número: ")        chute = int(chute_str)        if (chute < 1 or chute > 100):            print("Você deve digitar um número entre 1 e 100")            continue        print("Você digitou: ", chute)        acertou = numero_secreto == chute        maior = chute > numero_secreto        menor = chute < numero_secreto        if (acertou):            print("Você acertou!")            print("Sua pontuação total é {}".format(pontuacao))            break        else:            print("Você errou.")            if (maior):                print("O seu chute for maior que o numero secreto!")            elif (menor):                print("O seu chute for menor que o numero secreto!")            pontos_perdidos = abs(numero_secreto - chute)            pontuacao -= pontos_perdidos    print("Fim do jogo!")if (__name__ == "__main__"):    jogar()